/**
 * External function to retrieve the RSA private key for password decryption.
 *
 * SmartLearning calls this once to obtain the private key, then stores it
 * and uses it to decrypt passwords returned by the create_user and
 * get_new_users APIs.
 *
 * The RSA key pair is auto-generated by the plugin on install/upgrade.
 * Requires site admin privileges.
 *
 * @package    local_sm_estratoos_plugin
 * @copyright  2025 SmartMind Technologies
 * @license    http://www.gnu.org/copyleft/gpl.html GNU GPL v3 or later
 */

namespace local_sm_estratoos_plugin\external;

defined('MOODLE_INTERNAL') || die();

global $CFG;
require_once($CFG->libdir . '/externallib.php');

use external_api;
use external_function_parameters;
use external_single_structure;
use external_value;
use external_warnings;

/**
 * API to retrieve the RSA private key for SmartLearning integration.
 */
class get_encryption_key extends external_api {

    /**
     * Define input parameters.
     *
     * @return external_function_parameters
     */
    public static function execute_parameters(): external_function_parameters {
        return new external_function_parameters([]);
    }

    /**
     * Retrieve the RSA private key.
     *
     * @return array Private key and metadata.
     */
    public static function execute(): array {
        global $USER;

        // Validate parameters.
        self::validate_parameters(self::execute_parameters(), []);

        $warnings = [];

        // Check user is logged in and not guest.
        if (empty($USER->id) || isguestuser($USER)) {
            throw new \moodle_exception('invaliduser', 'local_sm_estratoos_plugin');
        }

        // Only site admins can retrieve the encryption key.
        if (!is_siteadmin()) {
            throw new \moodle_exception('accessdenied', 'local_sm_estratoos_plugin');
        }

        $privatekey = get_config('local_sm_estratoos_plugin', 'rsa_private_key');
        $publickey = get_config('local_sm_estratoos_plugin', 'rsa_public_key');

        if (empty($privatekey) || empty($publickey)) {
            // Keys not generated yet â€” try to generate now.
            require_once(__DIR__ . '/../../db/install.php');
            xmldb_local_sm_estratoos_plugin_generate_rsa_keypair();

            $privatekey = get_config('local_sm_estratoos_plugin', 'rsa_private_key');
            $publickey = get_config('local_sm_estratoos_plugin', 'rsa_public_key');
        }

        if (empty($privatekey)) {
            return [
                'success' => false,
                'private_key' => '',
                'public_key' => '',
                'algorithm' => '',
                'key_size' => 0,
                'message' => 'RSA key pair could not be generated. Check OpenSSL is available on the server.',
                'warnings' => $warnings,
            ];
        }

        return [
            'success' => true,
            'private_key' => $privatekey,
            'public_key' => $publickey,
            'algorithm' => 'RSA-OAEP',
            'key_size' => 2048,
            'message' => 'Use the private key to decrypt passwords encrypted by the plugin with openssl_private_decrypt() and OPENSSL_PKCS1_OAEP_PADDING.',
            'warnings' => $warnings,
        ];
    }

    /**
     * Define return structure.
     *
     * @return external_single_structure
     */
    public static function execute_returns(): external_single_structure {
        return new external_single_structure([
            'success' => new external_value(PARAM_BOOL, 'Whether the key was retrieved successfully'),
            'private_key' => new external_value(PARAM_RAW, 'PEM-encoded RSA private key for decryption'),
            'public_key' => new external_value(PARAM_RAW, 'PEM-encoded RSA public key (for reference)'),
            'algorithm' => new external_value(PARAM_TEXT, 'Encryption algorithm used (RSA-OAEP)'),
            'key_size' => new external_value(PARAM_INT, 'Key size in bits'),
            'message' => new external_value(PARAM_TEXT, 'Usage instructions or error message'),
            'warnings' => new external_warnings(),
        ]);
    }
}
